create table tb_donation (collector_id bigint, id bigint generated by default as identity, user_id bigint, city varchar(255), description TEXT, state varchar(255), primary key (id));
create table tb_password_recover (expiration timestamp(6) with time zone not null, id bigint generated by default as identity, email varchar(255) not null, token varchar(255) not null, primary key (id));
create table tb_role (id bigint generated by default as identity, authority varchar(255), primary key (id));
create table tb_user (id bigint generated by default as identity, address varchar(255), birth_date varchar(255), email varchar(255) unique, name varchar(255) unique, password varchar(255), phone varchar(255), primary key (id));
create table tb_user_role (role_id bigint not null, user_id bigint not null, primary key (role_id, user_id));
alter table if exists tb_donation add constraint FKahyvb28u4c8jgpo1255pa9yn2 foreign key (collector_id) references tb_user;
alter table if exists tb_donation add constraint FKptl33pe62bi0y5631q2o31igo foreign key (user_id) references tb_user;
alter table if exists tb_user_role add constraint FKea2ootw6b6bb0xt3ptl28bymv foreign key (role_id) references tb_role;
alter table if exists tb_user_role add constraint FK7vn3h53d0tqdimm8cp45gc0kl foreign key (user_id) references tb_user;
